class PedidosRepository extends EntityRepository
{
    public function findAllTotalPedidos()
    {
        return $this->getEntityManager()->createQuery("
            SELECT p.Material, p.DescripcionMaterial, sum(p.VolPedido) Suma
            FROM AraucoCSVBundle:Pedidos p
            WHERE p.StatusMovimientodeMcia = 'A'
            GROUP By p.Material
            ")->getResult();
    }

    public function findPedidosAsig($startWeek, $endWeek, $status)
    {
        return $this->getEntityManager()->createQuery("
            SELECT
                P.DocEntrega,
                P.PosPedido,
                P.OrgVenta,
                P.Eta,
                P.FPE,
                S.Material,
                S.Desc_Mat,
                P.VolPedido,
                SUM( S.M3 ) as M3,
                P.CodCliente,
                P.NomCliente,
                P.EntComp,
                P.PaisDestino,
                P.Destino,
                P.FPAN,
                P.FPD,
                P.FecDis,
                P.RoundVentas,
                P.MT,
                P.Nave,
                P.ClaseMaterial
            FROM
                AraucoCSVBundle:Pedidos P,
                AraucoCSVBundle:Stock S
            WHERE
                P.DocEntrega = S.Nro_Entrega
                AND P.PosPedido = S.Pos_Entrega
                AND P.Eta >='".$startWeek."'
                AND P.Eta <='".$endWeek."'
                AND P.StatusComplete = '".$status."'
                AND P.StatusMovimientodeMcia = 'A'
            GROUP BY
                P.DocEntrega,
                P.PosPedido
            ORDER BY
                P.DocEntrega,
                P.PosPedido")->getResult();
    }

